function checkCashRegister(price, cash, cid) {
  let changeOwed = cash - price;
  let changePaid = 0;
  let changeCurr = [];
  let changeHolder =JSON.parse(JSON.stringify(cid));
  console.log(changeHolder)
  let status = "";
  // Here is your change, ma'am.
  let hundreds = 0;
  let twentys = 0;
  let tens = 0;
  let fives = 0;
  let ones = 0;
  let quarters = 0;
  let dimes = 0;
  let nickels = 0;
  let pennies = 0;
  while(changePaid <= changeOwed){
    while(changeOwed >= 100 && cid[8][1]){
      hundreds++;
      changeOwed -= 100;
      cid[8][1] -= 100;
    }while(changeOwed >= 20 && cid[7][1] > 0){
      twentys++;
      changeOwed -= 20;
      cid[7][1] -= 20;
    }while(changeOwed >= 10 && cid[6][1]){
      tens++;
      changeOwed -= 10;
      cid[6][1] -= 10;
    }while(changeOwed >= 5 && cid[5][1]){
      fives++;
      changeOwed -=5;
      cid[5][1] -= 5;
    }while(changeOwed >= 1  && cid[4][1]){
      ones++;
      changeOwed -= 1;
      cid[4][1] -= 1;
    }while(changeOwed >= .25 && cid[3][1]){
      quarters++;
      changeOwed -= .25;
      cid[3][1] -= .25;
    }while(changeOwed >= .10 && cid[2][1]){
      dimes++;
      changeOwed -= .1;
      cid[2][1] -= .1;
    }while(changeOwed >= .05  && cid[1][1]){
      nickels++;
      changeOwed -= .05;
      cid[1][1] -= .05;
    }while(changeOwed > 0  && cid[0][1]){
      pennies++;
      changeOwed -= .01;
      cid[0][1] -= .01;
    }
  }
  if(changeOwed > 0){
    status = "INSUFFICIENT_FUNDS";
  }else if(cid[0][1] < .01 && !cid[1][1] && !cid[2][1] && !cid[3][1] && !cid[4][1] && !cid[5][1] && !cid[6][1] && !cid[7][1] && !cid[8][1]){
    //drawer is tapped!
    status = "CLOSED";
    console.log(changeHolder);
    changeCurr = changeHolder;
  }else{
    status = "OPEN";
    if(pennies){
    changeCurr.unshift(["PENNY", pennies * .01]);
  }
  if(nickels){
    changeCurr.unshift(["NICKEL", nickels * .05]);
  }
  if(dimes){
    changeCurr.unshift(["DIME", dimes * .1]);
  }
  if(quarters){
    changeCurr.unshift(["QUARTER", quarters * .25]);
  }
  if(ones){
    changeCurr.unshift(["ONE", ones * 1]);
  }
  if(fives){
    changeCurr.unshift(["FIVE", fives * 5]);
  }
  if(tens){
    changeCurr.unshift(["TEN", tens * 10]);
  }
  if(twentys){
    changeCurr.unshift(["TWENTY", twentys * 20]);
  }
  if(hundreds){
    changeCurr.unshift(["ONE HUNDRED", hundreds * 100]);
  } 
  }

  console.log(status, changeHolder);

  return {status: status, change: changeCurr};
}

